// import { AppBarComponent } from "../mui/app-bar";
import { SideBar } from "./SideBar";
import { Outlet, useParams } from "react-router-dom";
import { useState } from "react";
import Grow from '@mui/material/Grow';
import * as React from 'react';
import Box from '@mui/material/Box';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import { useDB } from './db-hook';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import ReactPlayer from 'react-player';

export const Video = () => {

    const { result, error } = useDB(`http://127.0.0.1:8000/api/v1/video/${useParams()['videoid']}`);

    if (result === undefined) console.log('load');
    else console.log(result);

    if (result === undefined) return (
        <></>
    );

    return (
        <>
        {/* <AppBarComponent/> */}
        <CssBaseline />
        {/* <Grow in={true}> */}


            <Box sx={{ display: 'flex' }}>
                <CssBaseline />
                <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
                    <Toolbar>
                        <Typography variant="h6" href="/" noWrap component="a"
                            sx={{
                                mr: 2,
                                display: { xs: 'none', md: 'flex' },
                                fontFamily: 'monospace',
                                fontWeight: 700,
                                letterSpacing: '.3rem',
                                color: 'inherit',
                                textDecoration: 'none',
                                paddingLeft: '1rem',
                                flexGrow: 1
                            }}
                        >
                            Home
                        </Typography>
                        <Typography variant="h6" component="a" href="/signup"
                            sx={{
                                mr: 2,
                                display: { xs: 'none', md: 'flex' },
                                fontWeight: 700,
                                color: 'inherit',
                                textDecoration: 'none',
                                paddingRight: '1rem'
                            }}>
                            Sign up
                        </Typography>
                        <Typography variant="h6" component="a" href="/login"
                            sx={{
                                mr: 2,
                                display: { xs: 'none', md: 'flex' },
                                fontWeight: 700,
                                color: 'inherit',
                                textDecoration: 'none',
                            }}>
                            Login
                        </Typography>
                    </Toolbar>
                </AppBar>
                {/* Users list */}
                <SideBar /> 
                <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                    <Toolbar />
                    <Container maxWidth="lg">

                        <ReactPlayer width={'100%'} height={'650px'} playing controls url={`${result.video}`} light={`${result.preview}`} />
                        <Typography variant="h4" sx={{ paddingTop: '1rem' }} gutterBottom>{result.name}</Typography>
                        <Typography variant="button" display="block" sx={{ paddingTop: '0.1rem', color: 'text.secondary' }} gutterBottom>Author: {result.user_obj.username}</Typography>

                    </Container>
                </Box>
            </Box>

        {/* </Grow> */}
        </>
    );
}
