// import { AppBarComponent } from "../mui/app-bar";
import { SideBar } from "./SideBar";
import { NavBar } from './NavBar';
import * as React from 'react';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import { useDB } from './db-hook';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { Link } from "@mui/material";
// import { Link, MemoryRouter, Route, Routes, useLocation } from 'react-router-dom';
import Pagination from '@mui/material/Pagination';
import PaginationItem from '@mui/material/PaginationItem';

let count = 1;
function Content() {
    const query = new URLSearchParams(location.search);
    const page = parseInt(query.get('page') || '1', 10);
    return (
      <Pagination
        page={page}
        count={count}
        renderItem={(item) => (
          <PaginationItem
            component={Link}
            to={`/${item.page === 1 ? '' : `?page=${item.page}`}`}
            {...item}
          />
        )}
      />
    );
}

const queryString = window.location.search;
const urlParams = new URLSearchParams(queryString);

let url = 'http://127.0.0.1:8000/api/v1/video/';
if (urlParams.get('page') !== null) {
    url = url.concat('?page=' + urlParams.get('page'));
}

export const MainPage = () => {
    const { result, error } = useDB(url);

    if (result === undefined) console.log('load');
    else {
        console.log(result);
        console.log(Math.ceil(result.count / 6));
        count = Math.ceil(result.count / 6);
    }

    if (result === undefined) return (
        <></>
    );

    return (
        <>
        {/* <AppBarComponent/> */}
        <CssBaseline />
        {/* <Grow in={true}> */}


            <Box sx={{ display: 'flex' }}>
                <CssBaseline />
                <NavBar />
                {/* Users list */}
                <SideBar /> 
                <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                    <Toolbar />
                    <Container maxWidth="lg">

                        <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
                            {result.results.map((video, index) => (
                                <Grid item xs={2} sm={4} md={4} key={index}>

                                    <Card sx={{ maxWidth: 345 }}>
                                        <CardMedia
                                            sx={{ height: 200 }}
                                            image={`${video.preview}`}
                                            title={`${video.name}`}
                                        />
                                        <CardContent>
                                            <Link href={`/video/${video.id}`} underline="none" style={{width: '100%'}}>
                                                <Typography gutterBottom variant="h5" component="div">
                                                    {video.name}
                                                </Typography>
                                            </Link>
                                        </CardContent>
                                    </Card>

                                </Grid>
                            ))}
                        </Grid>

                        <Content />

                    </Container>
                </Box>
            </Box>

        {/* </Grow> */}
        </>
    );
}
