// import { AppBarComponent } from "../mui/app-bar";
import { SideBar } from "./SideBar";
import { NavBar } from './NavBar';
import * as React from 'react';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import Container from '@mui/material/Container';
import TextField from '@mui/material/TextField';
import FormControl from '@mui/material/FormControl';
import { Typography } from "@mui/material";
import { useDB } from './db-hook';
import { useState, useEffect } from "react";
import axios from "axios";
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import AlertTitle from '@mui/material/AlertTitle';

export const Login = () => {
    const [login, setLogin] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [send, setSend] = useState(0);
    const [result, setResult] = useState();
    const [error, setError] = useState();

    if (error !== undefined) console.log(error);

    const handleLoginChange = (event) => {
        setLogin(event.target.value);
    };

    const handleEmailChange = (event) => {
        setEmail(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleSendChange = (event) => {
        setSend((send) => { return send + 1 });
    };

    useEffect(() => {
        if (send !== 0) {
            const getAllItems = () => {
                axios
                    .post(
                        "http://127.0.0.1:8000/api/v1/auth/users/",
                        {
                            "username": login,
                            "email": email,
                            "password": password
                        }
                    )
                    .then((res) => res.data)
                    .then((res) => {
                        setResult(res);
                        console.log(res);
                    })
                    .catch((err) => {
                        setError(JSON.parse(err.request.response));
                    })
            };

            getAllItems();
        }
    }, [send]);

    // if (result === undefined) console.log('load');
    // else {
    //     console.log(result);
    // }

    // if (result === undefined) return (
    //     <></>
    // );

    return (
        <>
        <CssBaseline />

        <Box sx={{ display: 'flex' }}>
            <CssBaseline />
            <NavBar />
            {/* Users list */}
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                <Toolbar />
                <Container maxWidth="sm" align="center">

                    <Typography gutterBottom variant="h5" component="div" align="center">Register</Typography>
                    <FormControl fullWidth sx={{ m: 1 }} variant="standard">
                        <TextField id="standard-basic" label="Login" variant="standard" onChange={handleLoginChange} />
                    </FormControl>
                    {/* { err.request.response
                        <Alert severity="error">
                            <AlertTitle>Error</AlertTitle>
                            This is an error alert — <strong>check it out!</strong>
                        </Alert>
                    } */}
                    <FormControl fullWidth sx={{ m: 1 }} variant="standard">
                        <TextField id="standard-basic" label="Email" variant="standard" onChange={handleEmailChange} />
                    </FormControl>
                    <FormControl fullWidth sx={{ m: 1 }} variant="standard">
                        <TextField id="standard-basic" label="Password" variant="standard" type="password" onChange={handlePasswordChange} />
                    </FormControl>
                    <Button variant="contained" size="large" onClick={handleSendChange}>
                        Submit
                    </Button>

                </Container>
            </Box>
        </Box>
        </>
    );
}
